sqlMap.select:
/* CmmPopUpSQL > selectCMM_POP_1000 */ /* CmmPopUpSQL > selectCMM_POP_1000 */ SELECT TOP 1 ob.wob_bk_no ,isnull(ltrim(rtrim(ob.cust_ord_no)), '') as cust_ord_no ,ob.wob_bk_no ,ob.trucker_cd as trsp_trdp_cd ,tt.eng_nm as trsp_trdp_nm ,tt.eng_addr as trsp_trdp_addr ,wh.wh_cd ,wh.wh_nm ,wh.wh_addr ,wh.wh_ofc_cd ,wh.wh_rmk ,ob.buyer_cd ,isnull(ob.buyer_nm, '') as buyer_nm ,isnull(ob.buyer_addr1, '') as buyer_addr1 ,dtc.PIC_NM as buyer_pic ,(select out_do_rmk from tb_ofc o where o.ofc_cd = #ofc_cd#) out_do_rmk ,bt.trdp_cd as bill_trdp_cd ,bt.eng_nm as bill_trdp_nm ,bt.eng_addr as bill_trdp_addr ,pt.trdp_cd as pck_trdp_cd ,pt.eng_nm as pck_trdp_nm ,pt.eng_addr as pck_trdp_addr ,ptc.PIC_NM as pck_pic ,ob.ref_no FROM tl_wh_ob ob JOIN tl_wh wh ON ob.wh_cd = wh.wh_cd LEFT JOIN tb_trdp pt ON pt.trdp_cd = wh.wh_ofc_cd+'MAINCMP' LEFT JOIN TB_TRDP_CNTC_PSON ptc on ptc.TRDP_CD = pt.TRDP_CD and ptc.REP_FLG = 'Y' and isnull(ptc.DELT_FLG, '') = 'N' LEFT JOIN tb_trdp bt ON bt.trdp_cd = #ofc_cd# + 'MAINCMP' LEFT JOIN tb_trdp tt ON tt.trdp_cd = ob.trucker_cd LEFT JOIN TB_TRDP_CNTC_PSON dtc on dtc.TRDP_CD = ob.buyer_cd and dtc.REP_FLG = 'Y' and isnull(dtc.DELT_FLG, '') = 'N' WHERE 1 = 1
AND OB.WOB_BK_NO = #wob_bk_no#
AND OB.WOB_BK_NO = (select Max(WOB_BK_NO) from TL_WH_OB where CUST_ORD_NO = #cust_ord_no#)

/* CmmPopUpSQL.xml > selectCMM_POP_0440_LIST */ SELECT A1.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY B.BKG_SEQ ASC) as ROWNUM , B.CLP_NO AS PLAN_NO , (SELECT CD_NM FROM TB_COM_CD_DTL DTL WHERE COM_CD = 'C224' AND DTL.CD_VAL = B.CLP_STS_CD) AS CON_STATUS , C.BKG_NO AS CARRIER_BKG_NO , C.BKG_SEQ , E.REF_NO AS FILLING_NO , E.BL_NO AS MASTER_BL_NO , C.POL_CD , C.POL_NM , C.POD_CD , C.POD_NM , C.ETD_DT_TM , C.ETA_DT_TM , C.CNTR_INFO AS CNTR_SUM , C.TRNK_VSL_CD , C.TRNK_VSL_NM+'/'+C.TRNK_VOY AS TRNK_VSL_NM , B.MODI_USRID AS ISSUED_BY , B.MODI_TMS AS ISSUED_AT FROM ( SELECT CNTR.CLP_NO FROM (SELECT * FROM TB_BKG_CNTR_LIST WHERE DELT_FLG = 'N'
AND CNTR_NO = #cntr_no#
AND REF_NO = #ref_no#
) CNTR GROUP BY CNTR.CLP_NO ) A LEFT JOIN TB_CLP B ON A.CLP_NO = B.CLP_NO LEFT JOIN TB_BKG C ON B.BKG_SEQ = C.BKG_SEQ AND C.DELT_FLG = 'N' LEFT OUTER JOIN TB_ADD_INFO_BND D ON D.INTG_BKG_SEQ = C.BKG_SEQ AND D.DELT_FLG = 'N' LEFT OUTER JOIN TB_INTG_BL E ON D.INTG_BL_SEQ = E.INTG_BL_SEQ AND E.AIR_SEA_CLSS_CD = 'S' AND E.BIZ_CLSS_CD = 'M' AND E.DELT_FLG = 'N' WHERE 1=1 AND B.DELT_FLG = 'N'
AND C.POL_CD = #pol_cd#
AND C.POD_CD = #pod_cd#
AND B.CLP_NO LIKE '%'+#plan_no#+'%'
) A1 WHERE A1.ROWNUM > #beginIdx# AND A1.ROWNUM <= #topCnt# ORDER BY PLAN_NO DESC
