sqlMap.select:
/** getVat_rt_dp_cnt **/ SELECT isnull(a.VAT_RT_DP_CNT, '') + '^@@^' + isnull(a.XCH_RT_DP_CNT, '') FROM tb_ofc a WHERE a.delt_flg = 'N'
AND a.ofc_cd = #s_ofc_cd#
AND a.use_flg = 'Y'

/** getTaxOpt **/ SELECT isnull(a.TAX_OPT, '') FROM tb_ofc a WHERE a.delt_flg = 'N'
AND a.ofc_cd = #s_ofc_cd#
AND a.use_flg = 'Y'

/* MdmCodeFreightSQL.xml => selectLCL_ACCT_LIST */ SELECT co_ofc_cd, cd_tp_cd, ref_cd, locl_nm ,CASE use_flg WHEN 'Y' THEN 1 ELSE 0 END AS use_flg ,CASE blk_flg WHEN 'Y' THEN 1 ELSE 0 END AS blk_flg FROM TB_LOCL_ACCT LOCL WHERE 1=1
AND LOCL.cd_tp_cd = #s_cd_tp_cd#
AND LOCL.ref_cd = #frt_cd_s#

sqlMap.insert:
/* MdmCodeFreightSQL.xml => insertMDM_MCM_01602_NEW */ INSERT INTO TB_LOCL_ACCT ( co_ofc_cd, cd_tp_cd, ref_cd, locl_nm, use_flg, blk_flg,
rgst_usrid, rgst_ofc_cd, rgst_tms, modi_usrid, modi_ofc_cd, modi_tms ) VALUES ( #co_ofc_cd#, #cd_tp_cd#, #ref_cd#, #locl_nm#, #use_flg#, #blk_flg#,
#rgst_usrid#, #rgst_ofc_cd#, getdate(), #modi_usrid#, #modi_ofc_cd#, GETUTCDATE() )
